import React, { useState, useEffect } from 'react';
import { getStatistics } from '../api';

const Statistics = ({ selectedMonth }) => {
  const [statistics, setStatistics] = useState({ totalSale: 0, totalSold: 0, totalNotSold: 0 });

  useEffect(() => {
    fetchStatistics();
  }, [selectedMonth]);

  const fetchStatistics = async () => {
    const { data } = await getStatistics({ month: selectedMonth });
    setStatistics(data);
  };

  return (
    <div>
      <h3>Statistics</h3>
      <p>Total Sale: ${statistics.totalSale}</p>
      <p>Total Sold Items: {statistics.totalSold}</p>
      <p>Total Not Sold Items: {statistics.totalNotSold}</p>
    </div>
  );
};

export default Statistics;
