import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { getBarChartData } from '../api';

const BarChart = ({ selectedMonth }) => {
  const [barData, setBarData] = useState([]);

  useEffect(() => {
    fetchBarChartData();
  }, [selectedMonth]);

  const fetchBarChartData = async () => {
    const { data } = await getBarChartData({ month: selectedMonth });
    setBarData(data);
  };

  const chartData = {
    labels: ['0-100', '101-200', '201-300', '301-400', '401-500', '501-600', '601-700', '701-800', '801-900', '901+'],
    datasets: [
      {
        label: 'Items per Price Range',
        data: barData,
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  return (
    <div>
      <h3>Price Range Bar Chart</h3>
      <Bar data={chartData} />
    </div>
  );
};

export default BarChart;
